import { useContext, useState } from 'react';

import Modal from '../UI/Modal';
import CartItem from './CartItem';
import classes from './Cart.module.css';
import CartContext from '../../store/cart-context';
import CheckoutForm from './CheckoutForm';
import { Fragment } from 'react/cjs/react.production.min';

const Cart = (props) => {
  const cartCtx = useContext(CartContext);
  const [checkOut, setcheckOut]=useState(false);
  const [isSubmitting, setIsSumbitting] = useState(false);
  const [didSubmit, setDidSubmit] = useState(false);
  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;

  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  };

  const cartItemAddHandler = (item) => {
    cartCtx.addItem(item);
  };

  const cartItems = (
    <ul className={classes['cart-items']}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item.id)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );

  const orderHandler =(event)=>{
    setcheckOut(true);
  }

  const orderSubmitHandler = async (userData)=>{
    setIsSumbitting(true);
    await fetch("https://react-app-de313-default-rtdb.firebaseio.com/orders.json", {
      method: "POST",
      body: JSON.stringify({
        user: userData,
        orderItems: cartCtx.items,
      })
    });
    setDidSubmit(true);

  }

  const ModalAction = (
    <div className={classes.actions}>

      <button className={classes["button--alt"]} onClick={props.onClose}>
        Close
      </button>
      {hasItems && (
        <button className={classes.button} onClick={orderHandler}>
          Order
        </button>
      )}
    </div>
  );
  const submittingModal = <p>Sending data to the server...</p>;
  const submittedModal = (
    <div>
      <p>Your order is succuessfully placed.</p>
      <button className={classes["button--alt"]} onClick={props.onClose}>
        Close
      </button>
    </div>
  );
  const cartModal = <Fragment>
    { cartItems };
      <div className={classes.total}>
    <span>Total Amount</span>
    <span>{totalAmount}</span>
  </div>;
  {
    checkOut && (
      <CheckoutForm
        onSubmit={orderSubmitHandler}
        onClose={props.onClose}
      ></CheckoutForm>
    )
  }
  {
    !checkOut && ModalAction
  }
  </Fragment>


  return (
    <Modal onClose={props.onClose}>
      {!isSubmitting && !didSubmit && cartModal}
      {isSubmitting }
    </Modal>
  );
};

export default Cart;
